import React from 'react';
import './EGdata.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { SocialIcon } from 'react-social-icons';

import { useState, useEffect } from 'react'


class EGdata extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            communityid: this.props.communityid,
            name: '',
            link: '',
            description: '',
            collections: [],        }
    }
    componentDidMount(){
        console.log('Component Did Mount '+this.state.communityid);
        // this.getCommunityData(this.state.communityid);

        //https://wiki.lyrasis.org/display/DSPACE/2017-05-11+DSpace+7+UI+Working+Group+Meeting+notes
        //https://wiki.lyrasis.org/dsdoc6x/dspace-development/rest-api
        //find communities with https://diglib.eg.org/rest/communities/
        //first for gch
        var id = '97846f9e-10b3-4c61-919b-ca65128c8e10';
        // var threedorid = 'ad14765c-e33f-469d-8503-52ecc14be4dc';
        // var gchid = '97846f9e-10b3-4c61-919b-ca65128c8e10';


        this.getCommunityData(id);
        this.getCollections(id);
        this.getAuthors(id);

        // console.log('pp'+this.state.collections)


    }

    async getCommunityData(id){


    await fetch('https://diglib.eg.org/rest/communities/'+id, 
    {
        method: 'GET',
        headers: {
            'Content-Type': 'multipart/form-data',
            'Access-Control-Allow-Origin': '*',
        }
    }
    )
    .then((response) => {
        // console.log("a");
        // console.log(response)
        if(response.status === 200){
            // console.log("SUCCESSS");
            return response.json();     
        }else if(response.status === 408){
            // console.log("SOMETHING WENT WRONG")
            this.setState({ requestFailed: true })
        }
    })
    .then((data) => {
        this.setState({name: data['name']});
        this.setState({link: data['link']});
        this.setState({description: data['introductoryText']});
        // this.setState({ isLoading: false, downlines: data.response })
        // console.log("DATA STORED")
    })
    .catch((error) => {
        // console.log("DATA aaa "+error)
        this.setState({ requestFailed: true })      
    })
    }
    async getCollections(id){


        await fetch('https://diglib.eg.org/rest/communities/'+id+'/collections', 
        {
            method: 'GET',
            headers: {
                'Content-Type': 'multipart/form-data',
                'Access-Control-Allow-Origin': '*',
            }
        }
        )
        .then((response) => {

            if(response.status === 200){
                return response.json();     
            }else if(response.status === 408){
                this.setState({ requestFailed: true })
            }
        })
        .then((data) => {
            console.log(data);

            // this.state.collections = data;
            // var c=0;
            var newcollections = [];



            for (var key in data) {
                console.log(data[key].uuid);
                // this.setState({name: data['name']});
                // this.state.collections.push(data[key].uuid);
                // var name = data[key].name;
                // var id = data[key].uuid;
                // var both = [name,id];
                var combined = { name: data[key].name, 
                                 id: data[key].uuid,
                                 link: 'https://diglib.eg.org'+data[key].link}                                 
                                 ;

                newcollections.push(combined);



            }
            this.setState({ collections: newcollections }) 


            // console.log('ss'+this.state.collections)
        })
        .catch((error) => {
            this.setState({ requestFailed: true })      
        })
    }

    async getAuthors(id){


        await fetch('https://diglib.eg.org/rest/communities/'+id+'/collections', 
        {
            method: 'GET',
            headers: {
                'Content-Type': 'multipart/form-data',
                'Access-Control-Allow-Origin': '*',
            }
        }
        )
        .then((response) => {

            if(response.status === 200){
                return response.json();     
            }else if(response.status === 408){
                this.setState({ requestFailed: true })
            }
        })
        .then((data) => {
            console.log(data);

            // this.state.collections = data;
            // var c=0;
            var newcollections = [];



            for (var key in data) {
                console.log(data[key].uuid);
                // this.setState({name: data['name']});
                // this.state.collections.push(data[key].uuid);
                // var name = data[key].name;
                // var id = data[key].uuid;
                // var both = [name,id];
                var combined = { name: data[key].name, 
                                 id: data[key].uuid,
                                 link: 'https://diglib.eg.org'+data[key].link}                                 
                                 ;

                newcollections.push(combined);



            }
            this.setState({ collections: newcollections }) 


            // console.log('ss'+this.state.collections)
        })
        .catch((error) => {
            this.setState({ requestFailed: true })      
        })
    }

    render(){
        return (
        <div>
            <br/>
            {/* Description of the event */}
            <Container >
                <Row className="justify-content-md-center">
                    <Col lg={9} className='container-title'>                
                    <div ><strong>{this.state.name}</strong> </div>
                    </Col>

                </Row>
                <Row className="justify-content-md-center">
                    <Col lg={9} className='container-text'>   
                    <div>
                    The EUROGRAPHICS Workshop on Graphics and Cultural Heritage (GCH) aims to foster an international dialogue between ICT experts and CH scientists to have a better understanding of the critical requirements for processing, managing, and delivering cultural information to a broader audience. The objective of the workshop is to present and showcase new developments within the overall process chain, from data acquisition, analysis and synthesis, 3D documentation, and data management, to new forms of interactive presentations and 3D printing solutions. Interdisciplinary approaches for analysis, classification and interpretation of cultural artefacts are particularly relevant to the event.
                    </div>
                    <br/>

                    <div><strong>Chair of steering committee:</strong> Dieter Fellner and Karina Rodriguez Echavarria</div>
                    <br/>
                    <div  dangerouslySetInnerHTML={{__html: this.state.description}}></div>
                    </Col>
                </Row>
                <Row className="justify-content-md-center">
                    <Col lg={9} className='container-socialmedia'>   
                    <div><strong>Social media: </strong>
                    <SocialIcon className='socialicon' bgColor="white" url="https://twitter.com/eg_gch" />
                    <SocialIcon className='socialicon' bgColor="white" url="https://www.facebook.com/GCH2022" />
                    <SocialIcon className='socialicon' bgColor="white" url="https://www.youtube.com/channel/UCplAAkkummTE5b9JHYRlrPA" />
                    </div>
                    </Col>
                </Row>
            </Container>

            {/* Bibliometrics */}

            <br></br>
            <Container fluid>
            <Row>
            <Col lg={1} className='container-bibiometrics years'>   
                <div >
                <i>Publication years</i>
                </div>
            </Col>
            <Col lg={1} className='container-bibiometrics papersdownload'>   
                <div >
                <i>Total Paper downloads</i>
                </div>
            </Col>
            {/* <Col lg={1} className='container-bibiometrics'>   
            <div>aaa</div>
            </Col>
            <Col lg={1} className='container-bibiometrics'>   
            <div>aaa</div>
            </Col>
            <Col lg={1} className='container-bibiometrics'>   
            <div>aaa</div>
            </Col>
            <Col lg={1} className='container-bibiometrics'>   
            <div>aaa</div>
            </Col> */}
            </Row>

            </Container>

            <br/>  
        </div>
        )
    }
}
export default EGdata;
