import React from 'react';
import './EGdata.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { useState, useEffect } from 'react'


class EGdata extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            communityid: this.props.communityid,
            name: '',
            link: '',
            description: '',
            collections: [],        }
    }
    componentDidMount(){
        console.log('Component Did Mount '+this.state.communityid);
        // this.getCommunityData(this.state.communityid);

        //https://wiki.lyrasis.org/display/DSPACE/2017-05-11+DSpace+7+UI+Working+Group+Meeting+notes
        //https://wiki.lyrasis.org/dsdoc6x/dspace-development/rest-api
        //find communities with https://diglib.eg.org/rest/communities/
        //first for gch
        var id = '97846f9e-10b3-4c61-919b-ca65128c8e10';
        // var threedorid = 'ad14765c-e33f-469d-8503-52ecc14be4dc';
        // var gchid = '97846f9e-10b3-4c61-919b-ca65128c8e10';


        this.getCommunityData(id);
        this.getCollections(id);

    }

    async getCommunityData(id){


    await fetch('https://diglib.eg.org/rest/communities/'+id, 
    {
        method: 'GET',
        headers: {
            'Content-Type': 'multipart/form-data',
            'Access-Control-Allow-Origin': '*',
        }
    }
    )
    .then((response) => {
        // console.log("a");
        // console.log(response)
        if(response.status === 200){
            // console.log("SUCCESSS");
            return response.json();     
        }else if(response.status === 408){
            // console.log("SOMETHING WENT WRONG")
            this.setState({ requestFailed: true })
        }
    })
    .then((data) => {
        this.setState({name: data['name']});
        this.setState({link: data['link']});
        this.setState({description: data['introductoryText']});
        // this.setState({ isLoading: false, downlines: data.response })
        // console.log("DATA STORED")
    })
    .catch((error) => {
        // console.log("DATA aaa "+error)
        this.setState({ requestFailed: true })      
    })
    }
    async getCollections(id){


        await fetch('https://diglib.eg.org/rest/communities/'+id+'/collections', 
        {
            method: 'GET',
            headers: {
                'Content-Type': 'multipart/form-data',
                'Access-Control-Allow-Origin': '*',
            }
        }
        )
        .then((response) => {

            if(response.status === 200){
                return response.json();     
            }else if(response.status === 408){
                this.setState({ requestFailed: true })
            }
        })
        .then((data) => {
            // console.log(data);

            // this.state.collections = data;
            var c=0;
            for (var key in data) {
                console.log(data[key].uuid);
                this.state.collections[c] =  data[key].uuid;
                // if (data.hasOwnProperty(key)) {
                //    console.log(data[key].id);
                // }
                c=c+1;
            }



            console.log('ss'+this.state.collections)
        })
        .catch((error) => {
            this.setState({ requestFailed: true })      
        })
        }



    render(){
        return (
        <div>
            <br></br>

            <h1>{this.state.name}</h1>
            {/* <h2>{this.props.subtitle}</h2> */}
            <div className="row justify-content-md-center">
            </div>
            <br></br>
            <br></br>
            <Container>
                <Row className="justify-content-md-center">
                <Col lg={7}>                
                <div className="content" dangerouslySetInnerHTML={{__html: this.state.description}}></div>
                </Col>
                <Col md={1}  className="vl"></Col>

                {this.props.description}
                <Col lg={2}><h5>Social media:</h5></Col>
                </Row>
            </Container>
            <br/>
            <Container>

                <Row className="justify-content-md-center">
                <Col md="auto" >                
                    <h5>Latest Proceedings </h5>
                    {this.state.collections}
                </Col>
                </Row>
                </Container>

                {/* <Col md={1}  className="vl"></Col>
                <Col md="auto">
                    <h5>Social media</h5>
                </Col> */}

            {/* <div className="row justify-content-md-center">
            <div className="col col-lg-7">

            </div>
            <div className="vl"></div>
            <div className="col col-sm">
            </div>
            <div className="vl"></div>
            <div className="col col-sm">
                Social media
            </div>
            </div> */}
                {/* </Row> */}
        </div>
        )
    }
}
export default EGdata;
