import React from 'react';
import './EGdata.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { SocialIcon } from 'react-social-icons';
import myPDFImage from '../images/pdf.svg'
import myMetadataImage from '../images/metadata.svg'
import { TagCloud } from 'react-tagcloud'

import { useState, useEffect } from 'react'
import randomColor from 'randomcolor';


class EGdata extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            communityid: this.props.communityid,
            name: '',
            link: '',
            description: '',
            collections: [],  
            latestcollection: '',     
            topics : [],
            thecolor: randomColor({
                count: 10,
                hue: 'green'
             }),
            }
    }
    componentDidMount(){
        console.log('Component Did Mount '+this.state.communityid);
        // this.getCommunityData(this.state.communityid);

        //https://wiki.lyrasis.org/display/DSPACE/2017-05-11+DSpace+7+UI+Working+Group+Meeting+notes
        //https://wiki.lyrasis.org/dsdoc6x/dspace-development/rest-api
        //find communities with https://diglib.eg.org/rest/communities/
        //first for gch
        var id = '97846f9e-10b3-4c61-919b-ca65128c8e10';
        var lastissueid = '512c154d-aa8c-48a2-8014-38bae0131176';
        // var threedorid = 'ad14765c-e33f-469d-8503-52ecc14be4dc';
        // var gchid = '97846f9e-10b3-4c61-919b-ca65128c8e10';


        this.getCommunityData(id);
        this.getCollections(id);
        this.getAuthors(id);
        this.getLastIssue(lastissueid);
        this.getTopics(id);

        // console.log('pp'+this.state.collections)


    }

    async getCommunityData(id){


    await fetch('https://diglib.eg.org/rest/communities/'+id, 
    {
        method: 'GET',
        headers: {
            'Content-Type': 'multipart/form-data',
            'Access-Control-Allow-Origin': '*',
        }
    }
    )
    .then((response) => {
        // console.log("a");
        // console.log(response)
        if(response.status === 200){
            // console.log("SUCCESSS");
            return response.json();     
        }else if(response.status === 408){
            // console.log("SOMETHING WENT WRONG")
            this.setState({ requestFailed: true })
        }
    })
    .then((data) => {
        this.setState({name: data['name']});
        this.setState({link: data['link']});
        this.setState({description: data['introductoryText']});
        // this.setState({ isLoading: false, downlines: data.response })
        // console.log("DATA STORED")
    })
    .catch((error) => {
        // console.log("DATA aaa "+error)
        this.setState({ requestFailed: true })      
    })
    }
    async getCollections(id){


        await fetch('https://diglib.eg.org/rest/communities/'+id+'/collections', 
        {
            method: 'GET',
            headers: {
                'Content-Type': 'multipart/form-data',
                'Access-Control-Allow-Origin': '*',
            }
        }
        )
        .then((response) => {

            if(response.status === 200){
                return response.json();     
            }else if(response.status === 408){
                this.setState({ requestFailed: true })
            }
        })
        .then((data) => {
            console.log(data);

            // this.state.collections = data;
            // var c=0;
            var newcollections = [];



            for (var key in data) {
                console.log(data[key].uuid);
                // this.setState({name: data['name']});
                // this.state.collections.push(data[key].uuid);
                // var name = data[key].name;
                // var id = data[key].uuid;
                // var both = [name,id];
                var combined = { name: data[key].name, 
                                 id: data[key].uuid,
                                 link: 'https://diglib.eg.org'+data[key].link}                                 
                                 ;

                newcollections.push(combined);



            }
            this.setState({ collections: newcollections }) 


            // console.log('ss'+this.state.collections)
        })
        .catch((error) => {
            this.setState({ requestFailed: true })      
        })
    }

    async getAuthors(id){
        await fetch('https://diglib.eg.org/rest/communities/'+id+'/collections', 
        {
            method: 'GET',
            headers: {
                'Content-Type': 'multipart/form-data',
                'Access-Control-Allow-Origin': '*',
            }
        }
        )
        .then((response) => {

            if(response.status === 200){
                return response.json();     
            }else if(response.status === 408){
                this.setState({ requestFailed: true })
            }
        })
        .then((data) => {
            console.log(data);

            // this.state.collections = data;
            // var c=0;
            var newcollections = [];



            for (var key in data) {
                console.log(data[key].uuid);
                // this.setState({name: data['name']});
                // this.state.collections.push(data[key].uuid);
                // var name = data[key].name;
                // var id = data[key].uuid;
                // var both = [name,id];
                var combined = { name: data[key].name, 
                                 id: data[key].uuid,
                                 link: 'https://diglib.eg.org'+data[key].link}                                 
                                 ;

                newcollections.push(combined);



            }
            this.setState({ collections: newcollections }) 


            // console.log('ss'+this.state.collections)
        })
        .catch((error) => {
            this.setState({ requestFailed: true })      
        })
    }
    async getLastIssue(id){
        await fetch('https://diglib.eg.org/rest/collections/'+id, 
        {
            method: 'GET',
            headers: {
                'Content-Type': 'multipart/form-data',
                'Access-Control-Allow-Origin': '*',
            }
        }
        )
        .then((response) => {
            // console.log("a");
            // console.log(response)
            if(response.status === 200){
                // console.log("SUCCESSS");
                return response.json();     
            }else if(response.status === 408){
                // console.log("SOMETHING WENT WRONG")
                this.setState({ requestFailed: true })
            }
        })
        .then((data) => {
            this.setState({name: data['name']});
            // var textcollections = data['introductoryText'];
            var textcollections = data['introductoryText'].replace(/src="\/static\/icons\/metadata.gif"/gi, "src=\""+myMetadataImage+"\"");

            // var textcollections = textcollections.replace(/<img src="\/static\/icons\/pdf.gif" border="0" height="16" width="16" alt=/gi, "<img src=\"static/media/pdf.008c13e77e9039ff777465ca5e576c54.svg\" border=\"0\" height=\"16\" width=\"16\" alt=");
            var textcollections = textcollections.replace(/src="\/static\/icons\/pdf.gif"/gi, "src=\""+myPDFImage+"\"");
            console.log("aaa");

            console.log(textcollections);
            this.setState({latestcollection: textcollections});
            // this.setState({ isLoading: false, downlines: data.response })
            // console.log("DATA STORED")
        })
        .catch((error) => {
            // console.log("DATA aaa "+error)
            this.setState({ requestFailed: true })      
        })
        }
        async getTopics(id){
            // https://diglib.eg.org/solr/search
            await fetch('https://diglib.eg.org/rest/collections/'+id, 
            {
                method: 'GET',
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Access-Control-Allow-Origin': '*',
                }
            }
            )
            .then((response) => {
                // console.log("a");
                // console.log(response)
                if(response.status === 200){
                    // console.log("SUCCESSS");
                    return response.json();     
                }else if(response.status === 408){
                    // console.log("SOMETHING WENT WRONG")
                    this.setState({ requestFailed: true })
                }
            })
            .then((data) => {

                var topics = [
                    { value: 'Applied computing', count: 29 },
                    { value: 'Computing methodologies', count: 27 },
                    { value: 'Human centered computing', count: 13 },
                    { value: 'Arts and humanities', count: 11 },
                    { value: 'Virtual reality', count: 7 },
                    { value: 'Digital libraries and archives', count: 5 },
                    { value: 'Fine arts', count: 5 },
                    { value: 'Image processing', count: 5 },
                    { value: 'Archaeology', count: 4 },
                    { value: 'Architecture (buildings)', count: 4 },
